import React, { useEffect, useState, useRef, useCallback } from "react";
import "leaflet/dist/leaflet.css";
import {
  MapContainer,
  TileLayer,
  Marker,
  Polyline,
  useMap
} from "react-leaflet";
import L from "leaflet";

import styles from "./MapComponent.module.css";

interface LocationPoint {
  latitude: number;
  longitude: number;
  timestamp: string;
}

// SVG BUS ICON (Your original one)
const vehicleIcon = new L.Icon({
  iconUrl: 'data:image/svg+xml;base64,',
  iconSize: [30, 30],
  iconAnchor: [15, 15],
  popupAnchor: [0, -20],
});

function MapComponent() {
  const [route, setRoute] = useState<LocationPoint[]>([]);
  const [currentIndex, setCurrentIndex] = useState<number>(0);
  const [playing, setPlaying] = useState<boolean>(false);
  const [elapsedTime, setElapsedTime] = useState<number>(0);
  const [distanceTravelled, setDistanceTravelled] = useState<number>(0);
  const [speed, setSpeed] = useState<number>(0);

  const animationRef = useRef<number | null>(null);
  const startTimeRef = useRef<number | null>(null);
  const lastTimestampRef = useRef<string | null>(null);
  const lastPositionRef = useRef<[number, number] | null>(null);
  const progressRef = useRef<number>(0);

  // Load route
  useEffect(() => {
    fetch("/dummy-route.json")
      .then((res) => res.json())
      .then((data: LocationPoint[]) => setRoute(data));
  }, []);

  // Reset when paused/stopped
  useEffect(() => {
    if (!playing) {
      setCurrentIndex(0);
      setElapsedTime(0);
      setDistanceTravelled(0);
      setSpeed(0);
      progressRef.current = 0;
      lastTimestampRef.current = null;
      lastPositionRef.current = null;
      if (animationRef.current) {
        cancelAnimationFrame(animationRef.current);
        animationRef.current = null;
      }
    }
  }, [playing, route]);

  const haversineDistance = (lat1: number, lon1: number, lat2: number, lon2: number) => {
    const toRad = (x: number) => (x * Math.PI) / 180;
    const R = 6371000;
    const dLat = toRad(lat2 - lat1);
    const dLon = toRad(lon2 - lon1);
    const a =
      Math.sin(dLat / 2) ** 2 +
      Math.cos(toRad(lat1)) * Math.cos(toRad(lat2)) *
      Math.sin(dLon / 2) ** 2;
    const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
    return R * c;
  };

  const animate = useCallback(() => {
    if (!playing || route.length === 0) return;

    const currentPoint = route[currentIndex];
    const nextIndex = currentIndex + 1;

    if (nextIndex >= route.length) {
      setPlaying(false);
      return;
    }

    const nextPoint = route[nextIndex];
    const currentTime = Date.now();
    if (!startTimeRef.current) startTimeRef.current = currentTime;
    const elapsed = (currentTime - startTimeRef.current) / 1000;
    setElapsedTime(elapsed);

    const segmentTime = (new Date(nextPoint.timestamp).getTime() - new Date(currentPoint.timestamp).getTime()) / 1000;
    const segmentElapsed = elapsed - (currentIndex * segmentTime);
    progressRef.current = Math.min(segmentElapsed / segmentTime, 1);

    const lat = currentPoint.latitude + (nextPoint.latitude - currentPoint.latitude) * progressRef.current;
    const lng = currentPoint.longitude + (nextPoint.longitude - currentPoint.longitude) * progressRef.current;

    if (lastPositionRef.current) {
      const dist = haversineDistance(lastPositionRef.current[0], lastPositionRef.current[1], lat, lng);
      setDistanceTravelled(prev => prev + dist);

      const timeDiff = (currentTime - (lastTimestampRef.current ? new Date(lastTimestampRef.current).getTime() : currentTime)) / 1000;
      if (timeDiff > 0) setSpeed(dist / timeDiff);
    }

    lastPositionRef.current = [lat, lng];
    lastTimestampRef.current = new Date().toISOString();

    if (progressRef.current >= 1) {
      setCurrentIndex(prev => Math.min(prev + 1, route.length - 1));
      progressRef.current = 0;
      startTimeRef.current = Date.now();
    }

    animationRef.current = requestAnimationFrame(animate);
  }, [playing, currentIndex, route]);

  useEffect(() => {
    if (playing) {
      startTimeRef.current = null;
      animationRef.current = requestAnimationFrame(animate);
    }
    return () => {
      if (animationRef.current) cancelAnimationFrame(animationRef.current);
    };
  }, [playing, animate]);

  if (route.length === 0) return <p>Loading route...</p>;

  const current = route[currentIndex];
  const next = route[Math.min(currentIndex + 1, route.length - 1)];
  const lat = current.latitude + (next.latitude - current.latitude) * progressRef.current;
  const lng = current.longitude + (next.longitude - current.longitude) * progressRef.current;
  const currentPos: [number, number] = [lat, lng];

const pathSoFar: [number, number][] = [
  ...route.slice(0, currentIndex + 1).map(p => [p.latitude, p.longitude] as [number, number]),
  currentPos as [number, number]
];


  return (
    <div
      className={styles.container}
      style={{
        backgroundImage: 'url("/background.jpg")',
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        backgroundRepeat: 'no-repeat',
      }}
    >
      <h2 className={styles.header}>üöó Vehicle Route Simulator</h2>

      <div className={styles.mapWrapper}>
        <MapContainer center={currentPos} zoom={14} scrollWheelZoom style={{ height: "60vh", width: "100%" }}>
          <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" attribution="&copy; OpenStreetMap contributors" />
          <Marker position={currentPos} icon={vehicleIcon} />
          <Polyline positions={pathSoFar as L.LatLngTuple[]} color="blue" />
          <RecenterMap coords={currentPos} />
        </MapContainer>
      </div>

      <div className={styles.controls}>
        <button onClick={() => setPlaying(!playing)} className={styles.button}>
          {playing ? "‚è∏ Pause" : "‚ñ∂Ô∏è Play"}
        </button>
        <button onClick={() => {
          setPlaying(false);
          setCurrentIndex(0);
          setElapsedTime(0);
          setDistanceTravelled(0);
          setSpeed(0);
          progressRef.current = 0;
          lastTimestampRef.current = null;
          lastPositionRef.current = null;
        }} className={styles.button}>
          üîÅ Reset
        </button>
      </div>

      <div className={styles.info}>
        <p><strong>Current Coordinates:</strong> {currentPos[0].toFixed(6)}, {currentPos[1].toFixed(6)}</p>
        <p><strong>Timestamp:</strong> {route[currentIndex].timestamp}</p>
        <p><strong>Elapsed Time:</strong> {Math.floor(elapsedTime)} sec</p>
        <p><strong>Distance Travelled:</strong> {distanceTravelled.toFixed(2)} m</p>
        <p><strong>Speed:</strong> {speed.toFixed(2)} m/s</p>
      </div>
    </div>
  );
}

function RecenterMap({ coords }: { coords: [number, number] }) {
  const map = useMap();
  useEffect(() => {
    map.setView(coords);
  }, [coords]);
  return null;
}

export default MapComponent;
