import React, { useEffect, useState, useRef, useCallback } from "react";
import {
  MapContainer,
  TileLayer,
  Marker,
  Polyline,
  useMap
} from "react-leaflet";
import L from "leaflet";
import "leaflet/dist/leaflet.css";

import styles from "./MapComponent.module.css";

interface LocationPoint {
  latitude: number;
  longitude: number;
  timestamp: string;
}

const vehicleIcon = new L.Icon({
  iconUrl: 'data:image/svg+xml;base64,',
  iconSize: [30, 30],
  iconAnchor: [15, 15],
  popupAnchor: [0, -20],
});

function haversineDistance(lat1: number, lon1: number, lat2: number, lon2: number) {
  const toRad = (x: number) => (x * Math.PI) / 180;
  const R = 6371000;
  const dLat = toRad(lat2 - lat1);
  const dLon = toRad(lon2 - lon1);
  const a =
    Math.sin(dLat / 2) ** 2 +
    Math.cos(toRad(lat1)) *
      Math.cos(toRad(lat2)) *
      Math.sin(dLon / 2) ** 2;
  const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
  return R * c;
}

const RecenterMap = ({ coords }: { coords: [number, number] }) => {
  const map = useMap();
  useEffect(() => {
    map.setView(coords);
  }, [coords]);
  return null;
};

export default function MapComponent() {
  const [route, setRoute] = useState<LocationPoint[]>([]);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [playing, setPlaying] = useState(false);
  const [elapsedTime, setElapsedTime] = useState(0);
  const [distanceTravelled, setDistanceTravelled] = useState(0);
  const [speed, setSpeed] = useState(0);
  const animationRef = useRef<number | null>(null);
  const startTimeRef = useRef<number | null>(null);
  const lastPositionRef = useRef<[number, number] | null>(null);
  const progressRef = useRef(0);

  useEffect(() => {
    fetch("/openroute-geojson.json")
      .then(res => res.json())
      .then(data => {
        const coords = data.features[0].geometry.coordinates.map(
          ([lng, lat]: [number, number], i: number) => ({
            latitude: lat,
            longitude: lng,
            timestamp: new Date(Date.now() + i * 3000).toISOString()
          })
        );
        setRoute(coords);
      });
  }, []);

  useEffect(() => {
    if (!playing) {
      cancelAnimationFrame(animationRef.current!);
      setCurrentIndex(0);
      setElapsedTime(0);
      setDistanceTravelled(0);
      setSpeed(0);
      progressRef.current = 0;
      return;
    }

    const animate = () => {
      if (!playing || currentIndex >= route.length - 1) {
        setPlaying(false);
        return;
      }

      const now = Date.now();
      const current = route[currentIndex];
      const next = route[currentIndex + 1];
      const segmentTime = 3;
      const elapsed = (now - (startTimeRef.current ?? now)) / 1000;
      progressRef.current = Math.min(elapsed / segmentTime, 1);

      const lat = current.latitude + (next.latitude - current.latitude) * progressRef.current;
      const lng = current.longitude + (next.longitude - current.longitude) * progressRef.current;

      const newPos: [number, number] = [lat, lng];

      if (lastPositionRef.current) {
        const dist = haversineDistance(
          lastPositionRef.current[0],
          lastPositionRef.current[1],
          lat,
          lng
        );
        setDistanceTravelled(prev => prev + dist);
        setSpeed(dist / 3);
      }

      if (progressRef.current >= 1) {
        setCurrentIndex(i => i + 1);
        startTimeRef.current = Date.now();
      }

      lastPositionRef.current = newPos;
      setElapsedTime(e => e + 1);
      animationRef.current = requestAnimationFrame(animate);
    };

    startTimeRef.current = Date.now();
    animationRef.current = requestAnimationFrame(animate);

    return () => cancelAnimationFrame(animationRef.current!);
  }, [playing, currentIndex, route]);

  if (!route.length) return <p>Loading...</p>;

  const current = route[currentIndex];
  const next = route[Math.min(currentIndex + 1, route.length - 1)];
  const lat = current.latitude + (next.latitude - current.latitude) * progressRef.current;
  const lng = current.longitude + (next.longitude - current.longitude) * progressRef.current;
  const currentPos: [number, number] = [lat, lng];

  const pathSoFar: [number, number][] = [
    ...route.slice(0, currentIndex + 1).map(p => [p.latitude, p.longitude] as [number, number]),
    currentPos
  ];

  return (
    <div className={styles.container}>
      <h2 className={styles.header}>🚗 Vehicle Route Simulator</h2>
      <div className={styles.mapWrapper}>
        <MapContainer center={currentPos} zoom={16} scrollWheelZoom={true} style={{ height: "100%", width: "100%" }}>
          <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />
          <Marker position={currentPos} icon={vehicleIcon} />
          <Polyline positions={pathSoFar} color="blue" />
          <RecenterMap coords={currentPos} />
        </MapContainer>
      </div>

      <div className={styles.controls}>
        <button onClick={() => setPlaying(!playing)} className={styles.button}>
          {playing ? "Pause" : "Play"}
        </button>
        <button
          onClick={() => {
            setPlaying(false);
            setCurrentIndex(0);
            setElapsedTime(0);
            setDistanceTravelled(0);
            setSpeed(0);
            progressRef.current = 0;
          }}
          className={styles.button}
        >
          Reset
        </button>
      </div>

      <div className={styles.info}>
        <p><strong>Coordinates:</strong> {currentPos[0].toFixed(6)}, {currentPos[1].toFixed(6)}</p>
        <p><strong>Elapsed Time:</strong> {elapsedTime}s</p>
        <p><strong>Distance:</strong> {distanceTravelled.toFixed(2)} meters</p>
        <p><strong>Speed:</strong> {speed.toFixed(2)} m/s</p>
      </div>
    </div>
  );
}
