import React, { useEffect, useState, useRef } from "react";
import 'leaflet/dist/leaflet.css';
// Removed leaflet-defaulticon-compatibility imports

import {
  MapContainer,
  TileLayer,
  Polyline,
  Marker,
  useMap
} from "react-leaflet";
import "leaflet/dist/leaflet.css";

import L from "leaflet";

interface LocationPoint {
  latitude: number;
  longitude: number;
  timestamp: string;
}

// Manual fix for default Leaflet icon paths
delete (L.Icon.Default.prototype as any)._getIconUrl;

L.Icon.Default.mergeOptions({
  iconRetinaUrl: require('leaflet/dist/images/marker-icon-2x.png'),
  iconUrl: require('leaflet/dist/images/marker-icon.png'),
  shadowUrl: require('leaflet/dist/images/marker-shadow.png'),
});

function MapComponent() {
  const [route, setRoute] = useState<LocationPoint[]>([]);
  const [currentIndex, setCurrentIndex] = useState<number>(0);
  const [playing, setPlaying] = useState<boolean>(false);
  const intervalRef = useRef<NodeJS.Timeout | null>(null);

  useEffect(() => {
    fetch("/dummy-route.json")
      .then((res) => res.json())
      .then((data: LocationPoint[]) => {
        setRoute(data);
      });
  }, []);

  useEffect(() => {
    if (playing && route.length > 0) {
      intervalRef.current = setInterval(() => {
        setCurrentIndex((prev) => {
          if (prev < route.length - 1) {
            return prev + 1;
          } else {
            if (intervalRef.current) clearInterval(intervalRef.current);
            return prev;
          }
        });
      }, 2000);
    }

    return () => {
      if (intervalRef.current) clearInterval(intervalRef.current);
    };
  }, [playing, route]);

  if (route.length === 0) return <p>Loading route...</p>;

  const currentPos: [number, number] = [
    route[currentIndex].latitude,
    route[currentIndex].longitude
  ];

  const pathSoFar: [number, number][] = route
    .slice(0, currentIndex + 1)
    .map((p) => [p.latitude, p.longitude]);

  return (
    <>
      <div style={{ height: "80vh", width: "100%" }}>
        <MapContainer center={currentPos} zoom={15} scrollWheelZoom={true} style={{ height: "100%", width: "100%" }}>
          <TileLayer
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
            attribution="&copy; OpenStreetMap contributors"
          />
          <Marker position={currentPos}></Marker>
          <Polyline positions={pathSoFar} color="blue" />
          <RecenterMap coords={currentPos} />
        </MapContainer>
      </div>

      <div style={{ marginTop: "20px", textAlign: "center" }}>
        <button onClick={() => setPlaying(!playing)} style={{ padding: "10px 20px", fontSize: "16px" }}>
          {playing ? "Pause" : "Play"}
        </button>
        <p><strong>Current Coordinates:</strong> {currentPos[0].toFixed(6)}, {currentPos[1].toFixed(6)}</p>
        <p><strong>Timestamp:</strong> {route[currentIndex].timestamp}</p>
      </div>
    </>
  );
}

function RecenterMap({ coords }: { coords: [number, number] }) {
  const map = useMap();
  useEffect(() => {
    map.setView(coords);
  }, [coords]);
  return null;
}

export default MapComponent;
